"use client";
import React, { useState } from "react";
import "./Product.css";

export default function Product() {
  const [cart, setCart] = useState([]);

  const products = [
  { id: 1, name: "iPhone 15 Pro", price: 1099.99, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRKY607oe6wpop5E0F5-4gK9X9h9ouMqzsmfg&s" },
  { id: 2, name: "Smartwatch", price: 1299.99, image: "data:image/jpeg;base64," },
  { id: 3, name: "Android Smartphone", price: 799.99, image: "data:image/jpeg;base64," },
  { id: 4, name: "AirPods Pro", price: 249.99, image: "data:image/jpeg;base64," },
  { id: 5, name: "Bluetooth Speaker", price: 99.99, image: "data:image/jpeg;base64," },
  { id: 6, name: "Laptop", price: 899.99, image: "https://images.unsplash.com/photo-1496181133206-80ce9b88a853?w=400" }
];




  const addToCart = (product) => {
    setCart((prev) => {
      const exists = prev.find((item) => item.id === product.id);
      if (exists)
        return prev.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (productId) => {
    setCart((prev) => {
      const item = prev.find((i) => i.id === productId);
      if (!item) return prev;
      if (item.quantity === 1) return prev.filter((i) => i.id !== productId);
      return prev.map((i) =>
        i.id === productId ? { ...i, quantity: i.quantity - 1 } : i
      );
    });
  };

  const removeProductCompletely = (productId) => {
    setCart((prev) => prev.filter((i) => i.id !== productId));
  };

  const totalItems = cart.reduce((t, i) => t + i.quantity, 0);
  const totalPrice = cart.reduce((t, i) => t + i.price * i.quantity, 0).toFixed(2);

  return (
    <div className="store-container">
      <h1>This is Product Page</h1>

      <h2>Products</h2>
      <div className="product-grid">
        {products.map((product) => {
          const cartItem = cart.find((i) => i.id === product.id);
          return (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} className="product-image" />
              <h3>{product.name}</h3>
              <p>${product.price.toFixed(2)}</p>
              {!cartItem ? (
                <button className="add-btn" onClick={() => addToCart(product)}>
                  Add to Cart
                </button>
              ) : (
                <div className="quantity-controls">
                  <button onClick={() => removeFromCart(product.id)}>-</button>
                  <span>{cartItem.quantity}</span>
                  <button onClick={() => addToCart(product)}>+</button>
                </div>
              )}
            </div>
          );
        })}
      </div>

      {/* CART SUMMARY (Glassmorphism) */}
      <div className="cart-summary">
        <h2>Cart Summary</h2>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            {cart.map((item) => (
              <div key={item.id} className="cart-item">
                <strong>{item.name}</strong> Ã— {item.quantity}
                <span>
                  <button className="id" onClick={() => removeFromCart(item.id)}>-</button>
                  <button className="id1" onClick={() => addToCart(item)}>+</button>
                  <button className="remove-all" onClick={() => removeProductCompletely(item.id)}>
                     Remove All
                  </button>
                </span>
              </div>
            ))}
            <div className="cart-totals">
              <p><strong>Total Items:</strong> {totalItems}</p>
              <p><strong>Total Price:</strong> ${totalPrice}</p>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
